import React, { useEffect, useState } from 'react'
import { View, Text, Button, StyleSheet } from 'react-native'
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import ApiService from '../ApiService';


type FriendsScreenParamList = {

}

type Props = NativeStackScreenProps<FriendsScreenParamList, 'FriendsScreen'>;

export default function FriendsScreen(props: any) {

  interface UserType {
    firstName: string,
    lastName: string,
    email: string,
    password: string,
  }


  const [eventUsers, setEventUsers]: any[] = useState([])
  const [toggleArray, setToggleArray]: any[] = useState([])




  let userList = props.route.params.userList.map((user: any) =>
    // console.log(user)
      <Button
        color={eventUsers.includes(user.email) ? 'green' : 'red'}
        title={user.firstName + " " + user.lastName}
        onPress={() => {
          // setToggleArray((toggleArray: any) => [...toggleArray, user.email])
          registerAttendee(user.email)
        }}
      />
    )




  function registerAttendee(email: any) {
    // setToggleArray(toggleArray =>  toggleArray.push(email));
    (eventUsers: any) => setEventUsers(eventUsers.push(email))
  }


  return (
    <View>
      <Text>

      {userList}
      </Text>
    </View>
  )
}

const styles = StyleSheet.create({
  btnClicked: {
    color: 'red'
  }
})
