import React, { useEffect, useState } from 'react'
import { View, Text, Button, StyleSheet} from 'react-native'
import { useNavigation } from '@react-navigation/native';
import ApiService from '../ApiService';



interface EventListScreenProps {
  user: string
}


export default function EventListScreen(props:any) {

  const [myEvents, setMyEvents] = useState([])


  const navigation = useNavigation();

  useEffect(() => {
    const currentUser = {user: props.route.params.user}
    ApiService.getEventsList(currentUser)
    // .then(events => console.log(events))
    .then(events => setMyEvents(events))
    .catch(err => console.log(err))
  },[],
  )

  console.log()


const attendedEvents = myEvents.map((event: any) =>
  {
    return <View style={styles.container} key={event._id}>
    <Text style={styles.label}>
      {event.eventName}
    </Text>
    <Text style={styles.label}>
      {event.venue}
    </Text>
    <Text style={styles.label}>
      {event.date}
    </Text>
    <Button
      color='blue'
      title='Go to Event'
      onPress={() => {
        navigation.navigate('SingleEventScreen', {
          eventId: event._id
        })
      }} />
     </View>
  }
  )


  return (
    <View style={styles.container}>
      <Text style={styles.header}>Current Arcs</Text>
        {attendedEvents ? attendedEvents: <Text>Placeholder</Text>}
    </View>
  )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    // width: 280,
    height:600,
    alignItems:'center',
    justifyContent: 'flex-start',
    backgroundColor: "rgb(20,20,30)"
  },
  label: {
    color: 'white',
    marginTop: 40,
    marginBottom: 5,
    // fontWeight:'bold'
    // marginRight:220
  },
  header: {
    color: 'white',
    marginTop: 40,
    marginBottom: 5,
    fontWeight:'bold'
    // marginRight:220
  },
  title: {
    color: 'white',
    fontSize: 50,
    fontWeight: 'bold'
  },
  button: {
    // justifyContent:center,
    justifyContent: 'center',
    width: 140,
    marginTop: 30,
    height: 40,
    backgroundColor: '#C996D4',
    borderRadius: 4,
    fontWeight: "bold"

  },

  input: {
    width:320,
    backgroundColor: 'black',
    borderColor: 'rgb(129,129,129)',
    borderWidth:1,
    color: 'white',
    height: 40,
    borderRadius: 4,
    padding: 10,
    // backgroundColor:
  },

  signup: {
    color: 'rgb(230,230,230)',
    marginTop: 15,
