import React, { useEffect, useState } from 'react'
import { View, Text, Button } from 'react-native'
import ApiService from '../ApiService'
import { useNavigation } from '@react-navigation/native';


interface SingleEventScreenProps {
  user: string
}

export default function SingleEventScreen(props:any) {

  const navigation = useNavigation();



const [event, setEvent] = useState<any[]>([])
const [paid, setPaid] = useState('false')

// console.log(props.route.params.user)

useEffect(() => {
  const currentEvent = {event: props.route.params.eventId}
  ApiService.getEvent(currentEvent)
  .then(events => setEvent(events))
  .then()
}
)

// useEffect(() => {
//   const currentEvent = {event: props.route.params.eventId}
//   ApiService.getEvent(currentEvent)
//   .then(paid => paid[0].arcsPaid[props.route.params.user] ? true : false
//   )

// }
// )





function payNow(event:any, user:any) {
  ApiService.updatePayment(event, user)
}


const details = event.map((event) => {
  return <Text>{event.eventName}</Text>

})



const myArc = event.map((event: any) => {

if (paid) {

  return <View key={event._id}>

  <Button
      color='blue'
      title={paid ? 'Paid!' : 'Pay Now'}
      onPress={() => {
        setPaid('true');
        payNow(event._id, props.route.params.user)
      }
        } />
  </View>
}

else {
  return <View key={event._id}>

  <Button
      color='pink'
      title={paid ? 'Paid!' : 'Pay Now'}
      onPress={() => {
        setPaid('true');
        payNow(event._id, props.route.params.user)
      }
        } />
  </View>

}
}
)




  return (
    <View>
      {details}
      {myArc}
      <Button color='black'
      title='Back to Events'
      onPress={() => {
        navigation.goBack()
      }}/>
    </View>
  )
}
