import React from 'react'
import {Text, StyleSheet, View, TextInput, Button, ScrollView} from 'react-native';
import { useForm, Controller} from 'react-hook-form';
import Constants from 'expo-constants';
import { useState, useEffect } from 'react'
import ApiService from '../ApiService'
import { NavigationContainer, NavigationProp } from '@react-navigation/native';
import { ParamsList } from '../screens/paramsList';
import CurrencyInput from 'react-native-currency-input';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';


export default function NewArcForm() {


  const navigation = useNavigation();

  const { register, setValue, handleSubmit, control, reset, formState: { errors } } = useForm({
    defaultValues: {
      eventName: '',
      date: '',
      venue: '',
      foodCost: '',
      drinksCost: '',
    }
  });

  const [eventName, setEventName] = useState("")
  const [date, setDate] = useState("")
  const [venue, setVenue] = useState("")
  const [foodCost, setFoodCost] = useState(0)
  const [drinksCost, setDrinksCost] = useState(0)
  const [userList, setUserList] = useState([])
  const [eventUsers, setEventUsers] = useState([])

  useEffect(() => {
    ApiService.getUsers()
    .then(users => {
      return setUserList(users);
    })
  },[],
  )

  // console.log(eventUsers)

  const onSubmit = function () {
    const newArc = {
      eventName: eventName,
      date: date,
      venue: venue,
      foodCost: foodCost,
      drinksCost: drinksCost,
    }
  }

  function onChangeEvent(arg : string) {
    setEventName(arg)
  };
  function onChangeDate(arg : string) {
    setDate(arg)
  };
  function onChangeVenue(arg : string) {
    setVenue(arg)
  };
  function onChangeFoodCost(arg : number) {
    console.log(arg)
    setFoodCost(arg)
  };
  function onChangeDrinksCost(arg : number) {
    setDrinksCost(arg)
  };

  function updateEventUsers(arg : any) {
    let eventUserObjects = [];
  }


  return (
    <View style={styles.container}>
      <View style={styles.button}>
        <Button color="black"
          title="ADD FRIENDS"
          onPress={()=> navigation.navigate('FriendsScreen', {
            userList: userList,
            updateEventUsers: updateEventUsers
          })}
        />
      </View>
    <Text style={styles.label}>Event Name</Text>
      <Controller
        control={control}
        render={({field: { value }}) => (
          <TextInput
            style={styles.input}
            onChangeText={value => onChangeEvent(value)}
            value={eventName}
          />
        )}
        name="eventName"
        rules={{ required: true }}
      />

      <Text style={styles.label}>Date</Text>
      <Controller
        control={control}
        render={({field: { value }}) => (
          <TextInput
            style={styles.input}
            onChangeText={value => onChangeDate(value)}
            value={date}
          />
        )}
        name="date"
        rules={{ required: true }}
      />
      <Text style={styles.label}>Venue</Text>
      <Controller
        control={control}
        render={({field: { value }}) => (
          <TextInput
            style={styles.input}
            onChangeText={value => onChangeVenue(value)}
            value={venue}
            />
            )}
            name="venue"
            rules={{ required: true }}
            />
      <Text style={styles.label}>Food Cost</Text>
      <Controller
        control={control}
        render={({field: { value }}) => (
          <CurrencyInput
          keyboardType = 'numeric'
          style={styles.input}
          onChangeValue={value => onChangeFoodCost(value)}
          prefix="Â£"
          delimiter=","
          separator="."
          precision={2}
          value={foodCost}
          onSubmitEditing={()=> {
            navigation.navigate('FoodScreen', {
              foodCost: foodCost
              // eventUsers:
            })}}
          />
        )}
        name="foodCost"
        rules={{ required: true }}
      />
      <Text style={styles.label}>Drinks Cost</Text>
      <Controller
        control={control}
        render={({field: { value }}) => (
          <TextInput
          keyboardType = 'numeric'
            style={styles.input}
            // onChangeText={value => onChangeDrinksCost(value)}
            // value={drinksCost}
          />
        )}
        name="drinksCost"
        rules={{ required: true }}
      />
       <View style={styles.button}>
        <Button color="black"
          title="CREATE ARC"
          onPress={()=>{onSubmit()}}
        />
      </View>
  </View>
  )
}

const styles = StyleSheet.create({
  container: {
    width: 280,
    height:600,
    alignItems:'center',
    alignContent: 'center'
  },
  label: {
    color: 'white',
    marginTop: 15,
    marginBottom: 5,
    // marginRight:220
  },
  button: {
    // justifyContent:center,
    justifyContent: 'center',
    width: 140,
    marginTop: 30,
    height: 40,
    backgroundColor: '#C996D4',
    borderRadius: 4,
    fontWeight: "bold"

  },

  input: {
    width:320,
    backgroundColor: 'black',
    borderColor: 'rgb(129,129,129)',
    borderWidth:1,
    color: 'white',
    height: 40,
    borderRadius: 4,
    padding: 10,
    // backgroundColor:
  },

  signup: {
    color: 'rgb(230,230,230)',
    marginTop: 15,
    marginBottom: 5,
    fontWeight: 'bold',
    fontSize: 30,
    // fontFamily: 'Josefin Sans'
  }


});
