import React, { useEffect, useState } from 'react'
import { View, Text, Button } from 'react-native'
import ApiService from '../ApiService'
import { useNavigation } from '@react-navigation/native';


interface SingleEventScreenProps {
  user: string
}

export default function SingleEventScreen(props:any) {

  const navigation = useNavigation();


  // console.log('props', props)



const [event, setEvent] = useState<any[]>([])
const [eventUsers, setEventUsers] = useState<any[]>([])
const [paid, setPaid] = useState(false)

// console.log(props.route.params.user)

useEffect(() => {
  const currentEvent = {event: props.route.params.eventId}
  ApiService.getEvent(currentEvent)
  .then(events => {
    setEvent(events);
    return events
  })
  .then(event => console.log('event',event))
  // .then(events => {
  //   setPaid(events[0].arcsPaid[props.route.params.user])
  //   return events;
  // })
  // .then((users) => setEventUsers(users[0].arcNameArray))
},[]
)

useEffect(() => {
  const currentEvent = {event: props.route.params.eventId}
  ApiService.getEvent(currentEvent)
  .then(answer => setPaid(answer[0].arcsPaid[props.route.params.user]))
},[]
)

useEffect(() => {
  const currentEvent = {event: props.route.params.eventId}
  ApiService.getEvent(currentEvent)
  .then(users => {console.log('users',users);
  return users})
  .then((users) => setEventUsers(users[0].arcNameArray.map(a => [a.firstName, a.lastName, a.email])))
},[]
)


function payNow(event:any, user:any) {
  ApiService.updatePayment(event, user)
}

//event details
const details = event.map((event) => {
  return <Text key={event._id}>{event.eventName}</Text>
})



//myArc Component
const myArc = event.map((event: any) => {

  return <View key={event._id}>
  <Button
      color='blue'
      title={paid ? 'Paid!' : 'Pay Now'}
      onPress={() => {
        payNow(event._id, props.route.params.user)
      }
    } />
  </View>

}
)

// let friendsArc;
    const eventZero = event

  //  const test = eventUsers.map(a => event[0].arcsFirstNames[a.email])

   const friendsArc = eventUsers.filter(a => a[2] !== event[0].creator).map((user:any, i) => {


      return <View>
      <Text key={i}>{event[0].arcFirstNames[user[2]]}</Text>
      <Text key={i}>{event[0].arcs[user[2]]}</Text>
      <Text key={i}>{event[0].arcsPaid[user[2]]}</Text>
      <Text key={i}>{event[0].arcItems[user[2]]}</Text>
      </View>

    })

    // event[0].arcsNameArray.filter(a => a.)



//Friends Components



  return (
    <View>
      <Text>Hi</Text>
      {details}
      {myArc}
      {friendsArc ? friendsArc : 'hi you'}
      <Button color='black'
      title='Back to Events'
      onPress={() => {
        navigation.goBack()
      }}/>
    </View>
  )
}
