import React, { useEffect, useState } from 'react'
import { View, Text, Button, StyleSheet} from 'react-native'
import { useNavigation } from '@react-navigation/native';
import ApiService from '../ApiService';



interface EventListScreenProps {
  user: string
}


export default function EventListScreen(props:any) {

  const [myEvents, setMyEvents] = useState([])


  const navigation = useNavigation();

  useEffect(() => {
    const currentUser = {user: props.route.params.user}
    ApiService.getEventsList(currentUser)
    // .then(events => console.log(events))
    .then(events => setMyEvents(events))
    .catch(err => console.log(err))
  },[],
  )

  // const {width, height} = require('Dimensions').get('window');


const attendedEvents = myEvents.map((event: any) =>
  {
    return <View style={styles.eventContainer} key={event._id}>
    <Text style={styles.eventHeader}>
      {event.eventName}
    </Text>
    <Text style={styles.label}>
      {event.venue}
    </Text>
    <Text style={styles.label}>
      {event.date}
    </Text>
    <View style={styles.button}>
    <Button
      title='Go to Event'
      onPress={() => {
        navigation.navigate('SingleEventScreen', {
          eventId: event._id
        })
      }} />
      </View>
     </View>
  }
  )


  return (
    <View style={styles.container}>
      <Text style={styles.header}>Current Arcs</Text>
        {attendedEvents ? attendedEvents: <Text>Placeholder</Text>}
    </View>
  )
}


const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // width: 280,
    height:1000,
    alignItems:'center',
    justifyContent: 'center',
    backgroundColor: "rgb(20,20,30)"
  },
  eventHeader: {
    color: 'white',
    fontSize: 15,
  },
  eventContainer: {
    flex: 1,
    paddingTop: 20,
    paddingBottom: 5,
    width: "90%",
    borderTopColor:'#C996D4',
    borderRightColor:"rgb(20,20,30)",
    borderLeftColor:"rgb(20,20,30)",
    borderWidth:1,
    // paddingBottom:0,
    // borderBottom:40,
    height:600,
    alignItems:'center',
    // justifyContent: 'flex-start',
    backgroundColor: "rgb(20,20,30)"
  },
  label: {
    color: 'white',
    marginTop: 5,
    marginBottom: 5,
    // fontWeight:'bold'
    // marginRight:220
  },
  header: {
    color: 'white',
    marginTop: 40,
    marginBottom: 20,
    fontWeight:'bold',
    fontSize: 30,
    // marginRight:220
  },
  title: {
    color: 'white',
    fontSize: 50,
    fontWeight: 'bold'
  },
  button: {
    justifyContent: 'center',
    width: 140,
    marginTop: 5,
    marginBottom: 10,
    height: 30,
    backgroundColor:'#29b6f6',
    borderRadius: 4,
    alignSelf: 'center',
    fontWeight: "bold"

  },

})
